package com.halal.web.sa.controller;import java.util.Map;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.web.servlet.ModelAndView;import com.halal.web.sa.core.exception.ApplicationException;import com.halal.web.sa.service.BaseService;public abstract class BaseController {	public ModelAndView performExecute(HttpServletRequest request, HttpServletResponse response, ModelAndView modelAndView) throws ApplicationException {		Map<String, Object> globalMap = null;		globalMap = (Map<String, Object>) getAPIServiceProvider().executeService(request,response);				processResponse(modelAndView, globalMap);		return modelAndView;			}		protected abstract BaseService getAPIServiceProvider();		protected abstract void processResponse(ModelAndView modelAndView, Map<String, Object> globalMap);	}